-- Volcando estructura para procedimiento cotizador.sp_u_cotizacion_001
DELIMITER //
CREATE PROCEDURE `sp_u_cotizacion_001`(
	IN `pcotd_id` INT,
	IN `pcotd_precio` DECIMAL(8,2),
	IN `pcotd_cant` INT,
	IN `pcotd_profit` DECIMAL(8,2),
	IN `pcot_id` INT
)
BEGIN
    -- Declaración de variables
    DECLARE pcotd_total DECIMAL(8,2);
    DECLARE pcotd_subtotal DECIMAL(8,2);
    
    -- Calcular subtotal y total
    SET pcotd_subtotal = pcotd_cant * pcotd_precio;  -- Subtotal = cantidad * precio
    SET pcotd_total = pcotd_subtotal + pcotd_profit; -- Total = subtotal + profit
    
    -- Actualizar detalle de cotización
    UPDATE td_cotizacion
    SET
        cotd_precio = pcotd_precio,
        cotd_cant = pcotd_cant,
        cotd_profit = pcotd_profit,
        cotd_subtotal = pcotd_subtotal, -- Asignar subtotal
        cotd_total = pcotd_total        -- Asignar total
    WHERE
        cotd_id = pcotd_id;
    
    -- Actualizar la cotización maestra con los nuevos subtotales y totales
    UPDATE tm_cotizacion
    SET
        cot_subtotal = (SELECT SUM(cotd_subtotal) FROM td_cotizacion WHERE cot_id = pcot_id AND cotd_tipo = 'D' AND est = 1), -- Sumar subtotales
        cot_profit = (SELECT SUM(cotd_profit) FROM td_cotizacion WHERE cot_id = pcot_id AND cotd_tipo = 'D' AND est = 1),      -- Sumar profit
        cot_total = (SELECT SUM(cotd_total) FROM td_cotizacion WHERE cot_id = pcot_id AND cotd_tipo = 'D' AND est = 1)         -- Sumar totales
    WHERE
        cot_id = pcot_id;

    -- Retornar ID actualizado
    SELECT pcotd_id AS 'cotd_id';
    
END//
DELIMITER ;

